import { useEffect } from "react";
import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import App from "./App";

export default function ShowCurrentTime(props){
    const [currentTime, setCurrentTime] = props.useState('');
    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentTime(new Date());
        }
        , 1000);
        return () => {
            clearInterval(timer);
        };
    }, []);
    return (
        <div>
            <h1>{currentTime.toLocaleTimeString()}</h1>
        </div>
    );
}
