import { useEffect } from "react";
import React from "react";

export default function ShowCurrentTime(props){
    const [currentTime, setCurrentTime] = props.useState(new Date());
    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentTime(new Date());
        }
        , 1000);
        return () => {
            clearInterval(timer);
        };
    }, []);
    return timer;


}
