import React, { useState, useEffect } from 'react';
export default function Timer(){
    const [timer, setTimer] = useState(0);
    useEffect(() => {
        const timer = setInterval(() => {
            setTimer(timer + 1);
        }
        , 1000);
        return () => clearInterval(timer);

    },[]);
    return (
        <div className="app">
            <h1>{timer}</h1>
        </div>
    );
}
